class FireflyMathEngine:
    def __init__(self, prime_limit=10000):
        self.prime_limit = prime_limit
        self.primes = self.generate_primes(prime_limit)
        self.gaps = self.compute_prime_gaps(self.primes)

    def generate_primes(self, limit):
        return list(sp.primerange(2, limit))

    def compute_prime_gaps(self, primes):
        return [b - a for a, b in zip(primes[:-1], primes[1:])]

